import React, { Component } from 'react'

class book extends Component {
    constructor(props) {
        super(props)
    }

    render() {
        const {img,name,writer,date,text} = this.props;
        return (
            <div className="bookContainer">
                <div className="img">
                    <img src={img}></img>
                </div>
                <div className="bookInfo">
                    <h2>{name}</h2>
                    <h3>{writer}</h3>
                    <p>{date}</p>
                    <p className="bookText">{text}</p>
                </div>
            </div>
        )
    }
}


export default book;
