import React, { Component } from 'react'
import { Link } from 'react-router-dom'


class navbar extends Component {

    responsiveMenu = document.querySelector(".responsiveMenu");

    getResponsiveMenu = () =>{
        console.log("omer");
        //responsiveMenu.classList.add("active");
    }

    render() {
        return (
                <div className="navbar">
                    <Link to="/" className="option">
                        <h2 className="logo">OA</h2>
                    </Link>
                    <ul className="responsiveMenu">
                        <Link to="/" className="option">
                            <li>Anasayfa</li>
                        </Link>
                        <Link to="/about">
                            <li>Hakkımda</li>
                        </Link>
                            <li className="dropDown">
                                <p>İlgi Alanları</p>                      
                                <ul className="dropDownMenu">
                                    <Link to="/places-Visited" className="option">
                                        <li>Ziyaret Edilen Yerler</li>
                                    </Link>
                                    <Link to="/books-Read" className="option">
                                        <li className="booksread">Okunan Kitaplar</li>
                                    </Link>
                                </ul>
                            </li>
                        <Link to="/contact" className="option">                
                            <li>İletişim</li>
                        </Link>
                    </ul>
                    <i onClick={this.getResponsiveMenu} className="fas fa-bars menu"></i>
                </div>
        )
    }
}

export default navbar;
